readonly "registerDynamicWebhooks": (options: typeof WebhookRegistrationDetails.Encoded) => Effect.Effect<typeof ContainerForRegisteredWebhooks.Type, HttpClientError.HttpClientError | ParseError | JiraV3ClientError<"ErrorCollection", typeof ErrorCollection.Type> | JiraV3ClientError<"ErrorCollection", typeof ErrorCollection.Type>>
